    private void btnCalculoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalculoActionPerformed
        
        String numeroInscripcion = txtInscripcion.getText();
        String nombre = txtNombre.getText();
        double patrimonio = Double.parseDouble(textPatrimonio.getText());
        int estrato = Integer.parseInt(txtEstrato.getText());
        
        Matricula matricula = new Matricula (numeroInscripcion, nombre, patrimonio, estrato);
        txtResultado.setText(matricula.mostrarDatos());

        
    }//GEN-LAST:event_btnCalculoActionPerformed

